/*Table structure for table `t_template_excel` */

DROP TABLE IF EXISTS `t_template_excel`;

CREATE TABLE `t_template_excel` (
  `templateid` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(50) DEFAULT NULL COMMENT '模板名称',
  `diskcode` int(11) DEFAULT '-1' COMMENT '硬盘编码',
  `theaternum` int(11) DEFAULT '-1' COMMENT '影院编码',
  `theatername` int(11) DEFAULT '-1' COMMENT '影院名称',
  `moviename` int(11) DEFAULT '-1' COMMENT '影片名称',
  `theaterchain` int(11) DEFAULT '-1' COMMENT '所属院线',
  `customer_phone` int(11) DEFAULT '-1' COMMENT '联系方式',
  `customer` int(11) DEFAULT '-1' COMMENT '联系人',
  `createtime` datetime DEFAULT NULL,
  `modifytime` datetime DEFAULT NULL,
  `start_row` int(11) DEFAULT '-1',
  `type` int(11) DEFAULT NULL COMMENT '模板类型，1-回盘模板，2-物料模板',
  `post` int(11) DEFAULT '-1' COMMENT '海报',
  `card` int(11) DEFAULT '-1' COMMENT '立牌',
  `exhibition` int(11) DEFAULT '-1' COMMENT '展架',
  `diskcardcount` int(11) DEFAULT '-1' COMMENT '硬盘数量',
  `recipient_address` int(11) DEFAULT '-1' COMMENT '收盘地址',
  PRIMARY KEY (`templateid`)
) ENGINE=InnoDB AUTO_INCREMENT=5 DEFAULT CHARSET=utf8 COMMENT='模板表';

