/*Table structure for table `t_theater_notice` */

DROP TABLE IF EXISTS `t_theater_notice`;

CREATE TABLE `t_theater_notice` (
  `noti_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '通知ID',
  `noti_type` int(11) DEFAULT '0' COMMENT '通知类型（0、发行通知；1、院线通知）',
