  `noti_content` longtext COMMENT '通知内容',
  `noti_status` int(11) DEFAULT '0' COMMENT '通知状态(0、正式使用；1、不可查阅；2、草稿)',
  `create_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `update_time` timestamp NULL DEFAULT NULL COMMENT '更新时间',
  `author` varchar(50) DEFAULT NULL COMMENT '作者',
  `cinemaid` int(11) DEFAULT NULL COMMENT '影院ID',
  `brows_times` int(11) DEFAULT NULL COMMENT '浏览次数',
  `noti_document_url` varchar(200) DEFAULT NULL COMMENT '文档保存相对路径',
